function eliminarDuplicadosYMantenerMasLargo(array) {
    // Verificar si el array está vacío
    if (!array || array.length === 0) return [];

    // Agrupar objetos por el campo "Nombre Pelicula CAS"
    const agrupadosPorNombre = array.reduce((acumulador, actual) => {
        const clave = actual["Nombre Pelicula CAS"];
        if (clave === undefined) return acumulador;

        if (!acumulador[clave]) {
            acumulador[clave] = [];
        }
        acumulador[clave].push(actual);
        return acumulador;
    }, {});

    // Para cada grupo, encontrar el objeto con la representación más larga
    const resultado = Object.values(agrupadosPorNombre).map(grupo => {
        return grupo.sort((a, b) => 
            JSON.stringify(b).length - JSON.stringify(a).length
        )[0];
    });

    return resultado;
}

