function eliminarDuplicadosYMantenerMasLargo(array) {
    if (!array || array.length === 0) return [];

    const agrupadosPorNombre = array.reduce((acumulador, actual) => {
        const clave = actual["Nombre Pelicula CAS"];
        if (clave === undefined) return acumulador;

        if (!acumulador[clave]) {
            acumulador[clave] = [];
        }
        acumulador[clave].push(actual);
        return acumulador;
    }, {});

    const resultado = Object.values(agrupadosPorNombre).map(grupo => {
        return grupo.sort((a, b) => JSON.stringify(b).length - JSON.stringify(a).length)[0];
    });

    return resultado;
}

const peliculasUnicas = eliminarDuplicadosYMantenerMasLargo($input.first().json.data);

const cleanTitles = peliculasUnicas
  .map(p => p["Nombre Pelicula CAS"])
  .filter(t => typeof t === "string" && t.trim() !== "");

// Divide en chunks
const chunkSize = 30;
const chunks = [];

for (let i = 0; i < cleanTitles.length; i += chunkSize) {
    chunks.push(cleanTitles.slice(i, i + chunkSize));
}

return chunks.map((titles) => ({
    json: {
        titlesToTranslate: titles,
        peliculasUnicas
    }
}));

const bloques = items[0].json.BloquesPeliculas || [];

const resultado = [];

for (const bloque of bloques) {
  // Extraer título
  const tituloMatch = bloque.match(/<h2 class="filmtitle.*?">(.*?)<span class="spacer">/s);
  const titulo = tituloMatch ? tituloMatch[1].trim() : "Título no disponible";

  // Extraer todas las sesiones con fecha y hora
  const sesionesMatch = bloque.match(/<div class="session">.*?<\/div>/gs);
  const sesionesCompletas = sesionesMatch ? sesionesMatch.map(session => {
    // Extraer día y hora
    const diaHoraMatch = session.match(/>([^<]+)</);
    if (diaHoraMatch) {
      const [dia, hora] = diaHoraMatch[1].trim().split(' ');
      return `${dia} ${hora}`; // Mantener día y hora juntos
    }
    return "Sesión no disponible";
  }) : ["Sesión no disponible"];

  // Formatear las sesiones como una cadena separada por punto y coma
  const sesionesFormateadas = sesionesCompletas.join('; ');

  // Agregar al resultado
  resultado.push({
    json: {
      "Nombre Pelicula CAS": titulo,
      "Fechas": sesionesFormateadas, // Ahora incluye día y hora
      "Horario": sesionesCompletas // Mantener como array para procesamiento posterior
    }
  });
}

return resultado;

